import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";

export const addTweetAsync = createAsyncThunk("tweets/create", async (data) => {
  return await axios.post("comments", data).then((res) => {
    return res.data;
  });
});

export const getTweetsAsync = createAsyncThunk("tweets/all", async () => {
  return await axios.get("comments").then((res) => {
    return res.data;
  });
});

const tweetSlice = createSlice({
  name: "tweets",
  initialState: {
    loading: false,
    tweets: [],
  },
  reducers: {
    removeTweet: (state, action) => {
      state.projects = state.projects.filter(
        (p) => p._id !== action.payload._id
      );
    },
  },
  extraReducers: (builder) => {
    builder.addCase(getTweetsAsync.pending, (state, action) => {
      state.loading = true;
    });
    builder.addCase(getTweetsAsync.fulfilled, (state, action) => {
      state.loading = false;
      state.tweets = action.payload;
    });

    builder.addCase(addTweetAsync.pending, (state, action) => {
      state.loading = true;
    });
    builder.addCase(addTweetAsync.fulfilled, (state, action) => {
      state.loading = false;
      state.tweets.unshift(action.payload);
    });
  },
});

export const { removeTweet } = tweetSlice.actions;

export const deleteTweetAsync = (id) => async (dispatch) => {
  await axios.delete(`comments/${id}`).then((res) => {
    dispatch(removeTweet(res.data.data));
  });
};

export default tweetSlice.reducer;
