import { createSlice } from "@reduxjs/toolkit";
import axios from "axios";

const industrySlice = createSlice({
  name: "industry",
  initialState: {
    industries: [],
  },
  reducers: {
    getIndustries: (state, action) => {
      state.industries = action.payload;
    },
  },
});

export const { getIndustries } = industrySlice.actions;

export const getIndustriesAsync = (data) => (dispatch) => {
  axios.get("http://localhost:4000/industries", data).then((res) => {
    dispatch(getIndustries(res.data));
  });
};

export default industrySlice.reducer;
