import * as React from "react";
import PropTypes from "prop-types";
import Button from "@mui/material/Button";
import { styled } from "@mui/material/styles";
import Dialog from "@mui/material/Dialog";
import DialogTitle from "@mui/material/DialogTitle";
import DialogContent from "@mui/material/DialogContent";
import DialogActions from "@mui/material/DialogActions";
import FormControl from "@mui/material/FormControl";
import FormLabel from "@mui/material/FormLabel";
import OutlinedInput from "@mui/material/OutlinedInput";
import IconButton from "@mui/material/IconButton";
import Stack from "@mui/material/Stack";
import { MdClose } from "react-icons/md";

import { closeDialog } from "@/store/reducers/tweetSlice";

import { useSelector, useDispatch } from "react-redux";

const BootstrapDialog = styled(Dialog)(({ theme }) => ({
  "& .MuiDialogContent-root": {
    padding: theme.spacing(2),
  },
  "& .MuiDialogActions-root": {
    padding: theme.spacing(1),
  },
}));

const BootstrapDialogTitle = (props) => {
  const { children, onClose, ...other } = props;

  return (
    <DialogTitle sx={{ m: 0, p: 2 }} {...other}>
      {children}
      {onClose ? (
        <IconButton
          aria-label="close"
          onClick={onClose}
          sx={{
            position: "absolute",
            right: 8,
            top: 8,
            color: (theme) => theme.palette.grey[500],
          }}
        >
          <MdClose />
        </IconButton>
      ) : null}
    </DialogTitle>
  );
};

BootstrapDialogTitle.propTypes = {
  children: PropTypes.node,
  onClose: PropTypes.func.isRequired,
};

export default function TweetDialog() {
  const dispatch = useDispatch();
  const {
    dialog: { open },
  } = useSelector(({ tweet }) => tweet);
  const [form, setForm] = React.useState({ name: "", body: "" });

  const handleChange = (ev) => {
    setForm({ ...form, [ev.target.name]: ev.target.value });
  };

  const handleClose = () => {
    dispatch(closeDialog());
  };

  return (
    <Dialog
      onClose={handleClose}
      aria-labelledby="customized-dialog-title"
      open={open}
    >
      <BootstrapDialogTitle id="dialog-title" onClose={handleClose}>
        Update tweet
      </BootstrapDialogTitle>
      <DialogContent dividers>
        <form noValidate className="w-full">
          <Stack spacing={2}>
            <FormControl fullWidth>
              <FormLabel>Name</FormLabel>
              <OutlinedInput
                name="name"
                value={form.name}
                onChange={handleChange}
              />
            </FormControl>

            <FormControl fullWidth>
              <FormLabel>Body</FormLabel>
              <OutlinedInput
                name="body"
                value={form.body}
                onChange={handleChange}
              />
            </FormControl>
          </Stack>

          <Button type="submit" variant="contained">
            Save
          </Button>
        </form>
      </DialogContent>
      <DialogActions>
        <Button autoFocus onClick={handleClose}>
          Save changes
        </Button>
      </DialogActions>
    </Dialog>
  );
}
