import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { addTweetAsync } from "@/store/reducers/tweetSlice";
import Button from "@mui/material/Button";
import CircularProgress from "@mui/material/CircularProgress";
import FormControl from "@mui/material/FormControl";
import FormLabel from "@mui/material/FormLabel";
import OutlinedInput from "@mui/material/OutlinedInput";
import Stack from "@mui/material/Stack";
import { useForm, Controller } from "react-hook-form";

const defaultValues = {
  name: "",
  body: "",
};

function NewTweet() {
  const dispatch = useDispatch();
  const [form, setForm] = useState({ id: null, name: "", body: "" });
  const isSubmitting = useSelector(({ tweet }) => tweet.isSubmitting);
  const { control, handleSubmit, reset } = useForm({ defaultValues });

  const canSubmit = () => {
    return form.name && form.body;
  };

  const handleChange = (ev) => {
    setForm({ ...form, [ev.target.name]: ev.target.value });
  };

  const handleSave = () => {
    dispatch(addTweetAsync(form));
  };

  return (
    <div className="w-full">
      <Stack spacing={2} mb={4}>
        <FormControl fullWidth>
          <FormLabel>Name</FormLabel>
          <Controller
            name="name"
            render={({ field }) => <OutlinedInput {...field} />}
          />
        </FormControl>

        <FormControl fullWidth>
          <FormLabel>Body</FormLabel>
          <OutlinedInput
            name="body"
            value={form.body}
            onChange={handleChange}
            rows={3}
            multiline
          />
        </FormControl>
      </Stack>

      <Button
        autoFocus
        variant="outlined"
        onClick={handleSave}
        disabled={!canSubmit()}
        endIcon={isSubmitting && <CircularProgress size={16} />}
      >
        Save tweet
      </Button>
    </div>
  );
}

export default NewTweet;
