import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { getTweetsAsync, openDialog } from "@/store/reducers/tweetSlice";
import { makeStyles } from "@mui/styles";
import Avatar from "@mui/material/Avatar";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemButton from "@mui/material/ListItemButton";
import ListItemText from "@mui/material/ListItemText";
import ListItemAvatar from "@mui/material/ListItemAvatar";
import IconButton from "@mui/material/IconButton";
import {
  HiDotsVertical,
  HiOutlinePencil,
  HiOutlineTrash,
} from "react-icons/hi";
import { FaRegUser } from "react-icons/fa";
import { CircularProgress } from "@mui/material";
import TweetDialog from "./dialogs/TweetDialog";

const useStyles = makeStyles((theme) => ({
  avatar: {
    "&.MuiAvatar-root": {
      backgroundColor: theme.palette.primary.main,
    },
  },
}));

const TweetsPage = () => {
  const dispatch = useDispatch();
  const classes = useStyles();
  const { tweets, loading } = useSelector(({ tweet }) => tweet);

  useEffect(() => {
    dispatch(getTweetsAsync());
  }, [dispatch]);

  console.log(tweets, "tweets");

  return (
    <div className=" max-w-6xl mx-auto p-8">
      <h2 className="text-4xl font-bold pb-8">Tweets</h2>

      <div className="grid grid-cols-12 gap-16">
        <div className="col-span-12 md:col-span-7">
          <h2 className="text-2xl font-medium text-gray-800 pb-6">
            Your tweets
          </h2>

          {loading ? (
            <CircularProgress />
          ) : (
            <List dense className="border-l-4 border-green-200">
              {tweets.map((a) => (
                <ListItem key={a.id} alignItems="flex-start">
                  <ListItemAvatar>
                    <Avatar className={classes.avatar}>
                      <FaRegUser size="24" />
                    </Avatar>
                  </ListItemAvatar>
                  <ListItemText
                    primary={a.name}
                    secondary={
                      <>
                        <span>{a.body}</span>
                        <span className="block space-x-4">
                          <IconButton
                            edge="end"
                            aria-label="delete"
                            onClick={(event) => {}}
                          >
                            <HiOutlinePencil size={16} />
                          </IconButton>
                          <IconButton
                            edge="end"
                            aria-label="delete"
                            onClick={(event) => {}}
                          >
                            <HiOutlineTrash size={16} />
                          </IconButton>
                        </span>
                      </>
                    }
                  />
                </ListItem>
              ))}
            </List>
          )}
        </div>

        <div className="col-span-12 md:col-span-5">
          <h2 className="text-2xl font-medium text-gray-800 pb-6">
            Tweet Comments
          </h2>
        </div>
      </div>

      <TweetDialog />
    </div>
  );
};

export default TweetsPage;
