import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { getArtistesAsync } from "@/store/reducers/artistSlice";
// import { getAlbumsAsync } from "@/store/reducers/albumSlice";
// import { getTweetByidAsync } from "@/store/reducers/tweetSlice";
import Avatar from "@mui/material/Avatar";
import IconButton from "@mui/material/IconButton";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemText from "@mui/material/ListItemText";
import ListItemAvatar from "@mui/material/ListItemAvatar";
import ListItemSecondaryAction from "@mui/material/ListItemSecondaryAction";
import moment from "moment";
import { HiFolder, HiTrash } from "react-icons/hi";
import { CircularProgress } from "@mui/material";

const Project = () => {
  const dispatch = useDispatch();
  const { artistes, loading } = useSelector(({ artist }) => artist);

  useEffect(() => {
    dispatch(getArtistesAsync());
  }, [dispatch]);

  console.log(artistes, "artistes");

  if (loading) {
    return <CircularProgress />;
  }

  return (
    <div className=" max-w-6xl mx-auto p-8">
      <h2 className="text-4xl font-bold pb-8">My artistes</h2>

      <div className="grid grid-cols-12 gap-16">
        <div className="col-span-12 md:col-span-5"></div>

        <div className="col-span-12 md:col-span-7">
          <h2 className="text-2xl font-medium text-gray-800 pb-6">
            Portfolio list
          </h2>
          <List dense className="border-l-4 border-green-200">
            {artistes.map((p) => (
              <ListItem key={p._id}>
                <ListItemAvatar>
                  <Avatar>
                    <HiFolder />
                  </Avatar>
                </ListItemAvatar>
                <ListItemText
                  primary={`${p.title} — ${p.slug}`}
                  secondary={
                    <>
                      <span>
                        {`${moment(p.startDate).format(
                          "DD MMM, yyyy"
                        )} — ${moment(p.endDate).format("DD MMM, yyyy")}`}
                      </span>
                      <br />
                      <span>{p.industry}</span>
                    </>
                  }
                />
                <ListItemSecondaryAction>
                  <IconButton
                    edge="end"
                    aria-label="delete"
                    // onClick={() => removePortfolio(p._id)}
                  >
                    <HiTrash />
                  </IconButton>
                </ListItemSecondaryAction>
              </ListItem>
            ))}
          </List>
        </div>
      </div>
    </div>
  );
};

export default Project;
