import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { getArtistesAsync } from "@/store/reducers/artistSlice";
import { getAlbumsAsync } from "@/store/reducers/albumSlice";
// import { getTweetByidAsync } from "@/store/reducers/tweetSlice";
import { makeStyles } from "@mui/styles";
import Avatar from "@mui/material/Avatar";
import IconButton from "@mui/material/IconButton";
import Button from "@mui/material/Button";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemText from "@mui/material/ListItemText";
import ListItemAvatar from "@mui/material/ListItemAvatar";
import ListItemSecondaryAction from "@mui/material/ListItemSecondaryAction";
import { HiUserCircle, HiOutlineExternalLink } from "react-icons/hi";
import { RiDiscLine } from "react-icons/ri";
import { CircularProgress } from "@mui/material";

const useStyles = makeStyles((theme) => ({
  avatar: {
    "&.MuiAvatar-root": {
      backgroundColor: theme.palette.primary,
    },
  },
}));

const ArtistesPage = () => {
  const dispatch = useDispatch();
  const classes = useStyles();
  const { artistes, loading } = useSelector(({ artist }) => artist);
  const { albums, loading: albumLoading } = useSelector(({ album }) => album);

  useEffect(() => {
    dispatch(getArtistesAsync());
  }, [dispatch]);

  console.log(artistes, "artistes");
  console.log(albums, "albums");
  console.log(albumLoading, "albumLoading");

  if (loading) {
    return (
      <div className=" max-w-6xl mx-auto p-8">
        <CircularProgress />
      </div>
    );
  }

  return (
    <div className=" max-w-6xl mx-auto p-8">
      <h2 className="text-4xl font-bold pb-8">Chocolate city artistes</h2>

      <div className="grid grid-cols-12 gap-16">
        <div className="col-span-12 md:col-span-7">
          <h2 className="text-2xl font-medium text-gray-800 pb-6">
            Artistes list
          </h2>
          <List dense className="border-l-4 border-green-200">
            {artistes.map((a) => (
              <ListItem key={a.id}>
                <ListItemAvatar>
                  <Avatar>
                    <HiUserCircle />
                  </Avatar>
                </ListItemAvatar>
                <ListItemText
                  primary={`${a.name} — ${a.email}`}
                  secondary={
                    <>
                      <span>
                        {a.phone} — {a.website}
                      </span>
                      <br />
                      <span>{a.website}</span>
                    </>
                  }
                />
                <ListItemSecondaryAction>
                  <Button
                    edge="start"
                    size="small"
                    aria-label="delete"
                    onClick={() => dispatch(getAlbumsAsync(a.id))}
                  >
                    view album
                  </Button>
                  <IconButton
                    edge="end"
                    aria-label="delete"
                    // onClick={() => removePortfolio(p._id)}
                  >
                    <HiOutlineExternalLink />
                  </IconButton>
                </ListItemSecondaryAction>
              </ListItem>
            ))}
          </List>
        </div>

        <div className="col-span-12 md:col-span-5">
          <h2 className="text-2xl font-medium text-gray-800 pb-6">
            Artiste's Album
          </h2>

          <List dense className="border-l-4 border-green-200">
            {albums.map((a) => (
              <ListItem key={a.id}>
                <ListItemAvatar>
                  <Avatar className={classes.avatar}>
                    <RiDiscLine />
                  </Avatar>
                </ListItemAvatar>
                <ListItemText
                  primary={`${a.title}`}
                  secondary={<span>owned by {artistes?.[a.userId]?.name}</span>}
                />
              </ListItem>
            ))}
          </List>
        </div>
      </div>
    </div>
  );
};

export default ArtistesPage;
